import { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Brain,
  Database,
  Clock,
  User,
  Search,
  Trash2,
  Edit2,
  Save,
  X,
  MessageSquare,
  Calendar,
  Home,
  FileText,
  Tag,
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { toast } from 'sonner';
import type { Bot } from '@/types';

interface MemoryEntry {
  id: string;
  key: string;
  value: string;
  category: string;
  scope: 'user' | 'family' | 'global';
  source: string;
  createdAt: Date;
  updatedAt: Date;
  accessCount: number;
}

interface MemoryContextPanelProps {
  bot: Bot;
  isOpen: boolean;
  onClose: () => void;
}

const mockMemories: MemoryEntry[] = [
  {
    id: 'mem-1',
    key: 'user.preferences.email_tone',
    value: 'Professional but friendly',
    category: 'preferences',
    scope: 'user',
    source: 'Email Assistant',
    createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 7),
    updatedAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 2),
    accessCount: 45,
  },
  {
    id: 'mem-2',
    key: 'family.calendar.preferred_times',
    value: 'Weekends mornings, weekday evenings after 6pm',
    category: 'calendar',
    scope: 'family',
    source: 'Calendar Bot',
    createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 14),
    updatedAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 5),
    accessCount: 23,
  },
  {
    id: 'mem-3',
    key: 'user.work.meeting_preferences',
    value: '30-min slots, no meetings before 9am',
    category: 'work',
    scope: 'user',
    source: 'Calendar Bot',
    createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 30),
    updatedAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 10),
    accessCount: 67,
  },
  {
    id: 'mem-4',
    key: 'home.devices.living_room_temp',
    value: '72┬░F',
    category: 'smarthome',
    scope: 'family',
    source: 'Smart Home Controller',
    createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 3),
    updatedAt: new Date(Date.now() - 1000 * 60 * 60 * 2),
    accessCount: 12,
  },
  {
    id: 'mem-5',
    key: 'user.shopping.grocery_list',
    value: 'Milk, Eggs, Bread, Coffee, Bananas',
    category: 'shopping',
    scope: 'user',
    source: 'Chores Bot',
    createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 1),
    updatedAt: new Date(Date.now() - 1000 * 60 * 30),
    accessCount: 8,
  },
];

const categoryIcons: Record<string, React.ElementType> = {
  preferences: User,
  calendar: Calendar,
  work: FileText,
  smarthome: Home,
  shopping: Tag,
  default: Database,
};

const scopeColors = {
  user: 'bg-blue-500/10 text-blue-500 border-blue-500/20',
  family: 'bg-emerald-500/10 text-emerald-500 border-emerald-500/20',
  global: 'bg-violet-500/10 text-violet-500 border-violet-500/20',
};

export default function MemoryContextPanel({ bot, isOpen, onClose }: MemoryContextPanelProps) {
  const [memories, setMemories] = useState<MemoryEntry[]>(mockMemories);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedScope, setSelectedScope] = useState<string>('all');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editValue, setEditValue] = useState('');

  const filteredMemories = memories.filter((memory) => {
    const matchesSearch =
      memory.key.toLowerCase().includes(searchQuery.toLowerCase()) ||
      memory.value.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesScope = selectedScope === 'all' || memory.scope === selectedScope;
    const matchesCategory = selectedCategory === 'all' || memory.category === selectedCategory;
    return matchesSearch && matchesScope && matchesCategory;
  });

  const categories = Array.from(new Set(memories.map((m) => m.category)));

  const handleDelete = (id: string) => {
    setMemories((prev) => prev.filter((m) => m.id !== id));
    toast.success('Memory deleted');
  };

  const handleEdit = (memory: MemoryEntry) => {
    setEditingId(memory.id);
    setEditValue(memory.value);
  };

  const handleSave = (id: string) => {
    setMemories((prev) =>
      prev.map((m) =>
        m.id === id ? { ...m, value: editValue, updatedAt: new Date() } : m
      )
    );
    setEditingId(null);
    toast.success('Memory updated');
  };

  const formatDate = (date: Date) => {
    const diff = Date.now() - date.getTime();
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    if (days === 0) return 'Today';
    if (days === 1) return 'Yesterday';
    if (days < 7) return `${days} days ago`;
    return date.toLocaleDateString();
  };

  if (!isOpen) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/50 backdrop-blur-sm"
        onClick={onClose}
      >
        <motion.div
          initial={{ opacity: 0, scale: 0.95, y: 20 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          exit={{ opacity: 0, scale: 0.95, y: 20 }}
          transition={{ duration: 0.2 }}
          className="relative w-full max-w-4xl max-h-[90vh] bg-card rounded-2xl border border-border shadow-2xl overflow-hidden"
          onClick={(e) => e.stopPropagation()}
        >
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-border">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-[#4F8CFF] to-[#7C5CFF] flex items-center justify-center shadow-lg shadow-primary/25">
                <Brain className="w-5 h-5 text-white" />
              </div>
              <div>
                <h2 className="text-lg font-semibold text-foreground">Memory & Context</h2>
                <p className="text-sm text-muted-foreground">
                  {bot.name} ΓÇó {memories.length} memories stored
                </p>
              </div>
            </div>
            <button onClick={onClose} className="p-2 rounded-lg hover:bg-muted transition-colors">
              <X className="w-5 h-5" />
            </button>
          </div>

          {/* Content */}
          <div className="p-6 overflow-y-auto max-h-[70vh]">
            {/* Stats */}
            <div className="grid grid-cols-3 gap-4 mb-6">
              <Card className="bg-card/50 backdrop-blur-sm border-border/50">
                <CardContent className="p-4">
                  <div className="flex items-center gap-2 mb-2">
                    <Database className="w-4 h-4 text-primary" />
                    <span className="text-sm text-muted-foreground">Total Memories</span>
                  </div>
                  <p className="text-2xl font-display font-bold text-foreground">{memories.length}</p>
                </CardContent>
              </Card>
              <Card className="bg-card/50 backdrop-blur-sm border-border/50">
                <CardContent className="p-4">
                  <div className="flex items-center gap-2 mb-2">
                    <Clock className="w-4 h-4 text-emerald-500" />
                    <span className="text-sm text-muted-foreground">Last Updated</span>
                  </div>
                  <p className="text-2xl font-display font-bold text-foreground">
                    {formatDate(memories[0]?.updatedAt || new Date())}
                  </p>
                </CardContent>
              </Card>
              <Card className="bg-card/50 backdrop-blur-sm border-border/50">
                <CardContent className="p-4">
                  <div className="flex items-center gap-2 mb-2">
                    <MessageSquare className="w-4 h-4 text-violet-500" />
                    <span className="text-sm text-muted-foreground">Access Count</span>
                  </div>
                  <p className="text-2xl font-display font-bold text-foreground">
                    {memories.reduce((sum, m) => sum + m.accessCount, 0).toLocaleString()}
                  </p>
                </CardContent>
              </Card>
            </div>

            {/* Filters */}
            <div className="flex flex-col sm:flex-row gap-4 mb-6">
              <div className="relative flex-1">
                <Search className="w-4 h-4 absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground" />
                <Input
                  placeholder="Search memories..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-9"
                />
              </div>
              <div className="flex gap-2">
                <select
                  value={selectedScope}
                  onChange={(e) => setSelectedScope(e.target.value)}
                  className="px-3 py-2 rounded-lg bg-muted border border-border text-sm"
                >
                  <option value="all">All Scopes</option>
                  <option value="user">User</option>
                  <option value="family">Family</option>
                  <option value="global">Global</option>
                </select>
                <select
                  value={selectedCategory}
                  onChange={(e) => setSelectedCategory(e.target.value)}
                  className="px-3 py-2 rounded-lg bg-muted border border-border text-sm"
                >
                  <option value="all">All Categories</option>
                  {categories.map((cat) => (
                    <option key={cat} value={cat}>
                      {cat.charAt(0).toUpperCase() + cat.slice(1)}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            {/* Memory List */}
            <div className="space-y-3">
              {filteredMemories.length === 0 && (
                <div className="text-center py-12">
                  <div className="w-16 h-16 rounded-full bg-muted flex items-center justify-center mx-auto mb-4">
                    <Database className="w-8 h-8 text-muted-foreground" />
                  </div>
                  <h3 className="text-lg font-semibold text-foreground mb-2">No memories found</h3>
                  <p className="text-muted-foreground">Try adjusting your search or filters</p>
                </div>
              )}

              {filteredMemories.map((memory) => {
                const Icon = categoryIcons[memory.category] || categoryIcons.default;
                return (
                  <Card
                    key={memory.id}
                    className="bg-card/50 backdrop-blur-sm border-border/50 hover:border-primary/30 transition-all"
                  >
                    <CardContent className="p-4">
                      <div className="flex items-start gap-4">
                        <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center flex-shrink-0">
                          <Icon className="w-5 h-5 text-primary" />
                        </div>
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center gap-2 mb-1">
                            <code className="text-sm font-mono text-foreground bg-muted px-2 py-0.5 rounded">
                              {memory.key}
                            </code>
                            <Badge
                              variant="outline"
                              className={`text-xs ${scopeColors[memory.scope]}`}
                            >
                              {memory.scope}
                            </Badge>
                            <Badge variant="secondary" className="text-xs">
                              {memory.category}
                            </Badge>
                          </div>

                          {editingId === memory.id ? (
                            <div className="flex gap-2 mt-2">
                              <Input
                                value={editValue}
                                onChange={(e) => setEditValue(e.target.value)}
                                className="flex-1"
                                autoFocus
                              />
                              <Button size="sm" onClick={() => handleSave(memory.id)}>
                                <Save className="w-4 h-4" />
                              </Button>
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => setEditingId(null)}
                              >
                                <X className="w-4 h-4" />
                              </Button>
                            </div>
                          ) : (
                            <p className="text-sm text-foreground mt-1">{memory.value}</p>
                          )}

                          <div className="flex items-center gap-4 mt-3 text-xs text-muted-foreground">
                            <span>Source: {memory.source}</span>
                            <span>ΓÇó</span>
                            <span>Updated {formatDate(memory.updatedAt)}</span>
                            <span>ΓÇó</span>
                            <span>{memory.accessCount} accesses</span>
                          </div>
                        </div>

                        {editingId !== memory.id && (
                          <div className="flex items-center gap-1">
                            <button
                              onClick={() => handleEdit(memory)}
                              className="p-2 rounded-lg hover:bg-muted transition-colors"
                            >
                              <Edit2 className="w-4 h-4 text-muted-foreground" />
                            </button>
                            <button
                              onClick={() => handleDelete(memory.id)}
                              className="p-2 rounded-lg hover:bg-red-500/10 transition-colors"
                            >
                              <Trash2 className="w-4 h-4 text-red-500" />
                            </button>
                          </div>
                        )}
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
}

import { useState } from 'react';
import { motion } from 'framer-motion';
import {
  Zap,
  TrendingUp,
  AlertTriangle,
  CheckCircle,
  Clock,
  BarChart3,
  ChevronRight,
} from 'lucide-react';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import type { Plan } from '@/types';

interface RateLimitIndicatorProps {
  plan: Plan;
  showDetails?: boolean;
}

interface UsageStats {
  hourly: number;
  daily: number;
  weekly: number;
  monthly: number;
}

export default function RateLimitIndicator({ plan, showDetails = false }: RateLimitIndicatorProps) {
  const [usage] = useState<UsageStats>({
    hourly: 45,
    daily: 342,
    weekly: 2156,
    monthly: plan.usedExecutions,
  });

  const [isExpanded, setIsExpanded] = useState(showDetails);

  const monthlyPercentage = Math.min(100, (usage.monthly / plan.executionQuota) * 100);
  const dailyPercentage = Math.min(100, (usage.daily / (plan.executionQuota / 30)) * 100);
  const hourlyPercentage = Math.min(100, (usage.hourly / (plan.executionQuota / 30 / 24)) * 100);

  const getStatusColor = (percentage: number) => {
    if (percentage < 50) return 'text-emerald-500';
    if (percentage < 75) return 'text-amber-500';
    if (percentage < 90) return 'text-orange-500';
    return 'text-red-500';
  };

  const getStatusBg = (percentage: number) => {
    if (percentage < 50) return 'bg-emerald-500';
    if (percentage < 75) return 'bg-amber-500';
    if (percentage < 90) return 'bg-orange-500';
    return 'bg-red-500';
  };

  const getStatusIcon = (percentage: number) => {
    if (percentage < 75) return CheckCircle;
    if (percentage < 90) return AlertTriangle;
    return AlertTriangle;
  };

  const StatusIcon = getStatusIcon(monthlyPercentage);

  const formatNumber = (num: number) => {
    if (num >= 1000000) return `${(num / 1000000).toFixed(1)}M`;
    if (num >= 1000) return `${(num / 1000).toFixed(1)}K`;
    return num.toString();
  };

  return (
    <Card className="bg-card/50 backdrop-blur-sm border-border/50">
      <CardContent className="p-4">
        {/* Main Indicator */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-3">
            <div className={`w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center`}>
              <Zap className="w-5 h-5 text-primary" />
            </div>
            <div>
              <p className="font-medium text-foreground">Execution Quota</p>
              <p className="text-sm text-muted-foreground">
                {formatNumber(usage.monthly)} / {formatNumber(plan.executionQuota)} executions
              </p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <StatusIcon className={`w-5 h-5 ${getStatusColor(monthlyPercentage)}`} />
            <span className={`text-sm font-medium ${getStatusColor(monthlyPercentage)}`}>
              {monthlyPercentage.toFixed(1)}%
            </span>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="relative h-2 bg-muted rounded-full overflow-hidden mb-2">
          <motion.div
            initial={{ width: 0 }}
            animate={{ width: `${monthlyPercentage}%` }}
            transition={{ duration: 0.5, ease: 'easeOut' }}
            className={`absolute h-full rounded-full ${getStatusBg(monthlyPercentage)}`}
          />
        </div>

        {/* Details Toggle */}
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="flex items-center gap-1 text-sm text-muted-foreground hover:text-foreground transition-colors"
        >
          {isExpanded ? 'Hide details' : 'Show details'}
          <ChevronRight className={`w-4 h-4 transition-transform ${isExpanded ? 'rotate-90' : ''}`} />
        </button>

        {/* Expanded Details */}
        {isExpanded && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className="mt-4 pt-4 border-t border-border space-y-4"
          >
            {/* Hourly Usage */}
            <div>
              <div className="flex items-center justify-between mb-1">
                <span className="text-sm text-muted-foreground flex items-center gap-2">
                  <Clock className="w-3 h-3" />
                  Hourly
                </span>
                <span className="text-sm text-foreground">{usage.hourly} executions</span>
              </div>
              <div className="h-1.5 bg-muted rounded-full overflow-hidden">
                <div
                  className={`h-full rounded-full ${getStatusBg(hourlyPercentage)}`}
                  style={{ width: `${hourlyPercentage}%` }}
                />
              </div>
            </div>

            {/* Daily Usage */}
            <div>
              <div className="flex items-center justify-between mb-1">
                <span className="text-sm text-muted-foreground flex items-center gap-2">
                  <TrendingUp className="w-3 h-3" />
                  Daily
                </span>
                <span className="text-sm text-foreground">{usage.daily} executions</span>
              </div>
              <div className="h-1.5 bg-muted rounded-full overflow-hidden">
                <div
                  className={`h-full rounded-full ${getStatusBg(dailyPercentage)}`}
                  style={{ width: `${dailyPercentage}%` }}
                />
              </div>
            </div>

            {/* Weekly Usage */}
            <div>
              <div className="flex items-center justify-between mb-1">
                <span className="text-sm text-muted-foreground flex items-center gap-2">
                  <BarChart3 className="w-3 h-3" />
                  Weekly
                </span>
                <span className="text-sm text-foreground">{usage.weekly} executions</span>
              </div>
              <div className="h-1.5 bg-muted rounded-full overflow-hidden">
                <div
                  className={`h-full rounded-full ${getStatusBg(
                    Math.min(100, (usage.weekly / (plan.executionQuota / 4)) * 100)
                  )}`}
                  style={{ width: `${Math.min(100, (usage.weekly / (plan.executionQuota / 4)) * 100)}%` }}
                />
              </div>
            </div>

            {/* Plan Info */}
            <div className="p-3 rounded-lg bg-muted space-y-2">
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Current Plan</span>
                <Badge variant="secondary" className="text-xs">
                  {plan.name}
                </Badge>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Channel Limit</span>
                <span className="text-sm text-foreground">{plan.channelLimit}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Pack Limit</span>
                <span className="text-sm text-foreground">{plan.packLimit}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Family Seats</span>
                <span className="text-sm text-foreground">{plan.familySeats}</span>
              </div>
              {plan.expiresAt && (
                <div className="flex items-center justify-between">
                  <span className="text-sm text-muted-foreground">Expires</span>
                  <span className="text-sm text-foreground">
                    {new Date(plan.expiresAt).toLocaleDateString()}
                  </span>
                </div>
              )}
            </div>

            {/* Warning */}
            {monthlyPercentage >= 90 && (
              <div className="flex items-start gap-2 p-3 rounded-lg bg-red-500/10 border border-red-500/20">
                <AlertTriangle className="w-5 h-5 text-red-500 flex-shrink-0 mt-0.5" />
                <div>
                  <p className="text-sm font-medium text-red-500">Quota Almost Exhausted</p>
                  <p className="text-xs text-red-500/80 mt-1">
                    You have used {monthlyPercentage.toFixed(0)}% of your monthly quota. Consider upgrading
                    your plan to avoid service interruption.
                  </p>
                </div>
              </div>
            )}
          </motion.div>
        )}
      </CardContent>
    </Card>
  );
