                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </header>

        {/* Content */}
        <main className="flex-1 p-4 sm:p-6 lg:p-8 overflow-auto">
          <AnimatePresence mode="wait">
            <motion.div
              key={activePanel}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              {renderPanel()}
            </motion.div>
          </AnimatePresence>
        </main>
      </div>

      {/* Bot Creation Wizard */}
      <BotCreationWizard 
        isOpen={wizardOpen} 
        onClose={() => setWizardOpen(false)} 
      />

      {/* Approval Workflow */}
      <ApprovalWorkflow
        isOpen={approvalWorkflowOpen}
