// Notification Store
interface NotificationState {
  notifications: Notification[];
  unreadCount: number;
  addNotification: (notification: Omit<Notification, 'id' | 'createdAt' | 'read'>) => void;
  markAsRead: (id: string) => void;
  markAllAsRead: () => void;
  dismissNotification: (id: string) => void;
}

export const useNotificationStore = create<NotificationState>()(
  persist(
    (set) => ({
      notifications: [],
      unreadCount: 0,
      addNotification: (notification) => {
        const newNotification: Notification = {
          ...notification,
          id: `notif-${Date.now()}`,
          read: false,
          createdAt: new Date(),
        };
        set((state) => ({
          notifications: [newNotification, ...state.notifications],
          unreadCount: state.unreadCount + 1,
        }));
      },
      markAsRead: (id) => {
        set((state) => ({
          notifications: state.notifications.map(n =>
            n.id === id ? { ...n, read: true } : n
          ),
          unreadCount: Math.max(0, state.unreadCount - 1),
        }));
      },
      markAllAsRead: () => {
        set((state) => ({
          notifications: state.notifications.map(n => ({ ...n, read: true })),
          unreadCount: 0,
        }));
      },
      dismissNotification: (id) => {
        set((state) => ({
          notifications: state.notifications.filter(n => n.id !== id),
          unreadCount: Math.max(0, state.unreadCount - 1),
        }));
      },
    }),
    {
      name: 'clawd-notifications',
    }
  )
);

// Onboarding Store
interface OnboardingStateStore {
  isOnboarding: boolean;
  currentStep: number;
  state: OnboardingState;
  startOnboarding: () => void;
  nextStep: () => void;
  prevStep: () => void;
  updateState: (updates: Partial<OnboardingState>) => void;
  completeOnboarding: () => void;
  resetOnboarding: () => void;
}

export const useOnboardingStore = create<OnboardingStateStore>()(
  persist(
    (set) => ({
      isOnboarding: false,
      currentStep: 1,
      state: {
        step: 1,
        selectedPacks: [],
        selectedChannels: [],
        channelConfigs: {},
      },
      startOnboarding: () => {
        set({ 
          isOnboarding: true, 
          currentStep: 1,
          state: {
            step: 1,
            selectedPacks: [],
            selectedChannels: [],
            channelConfigs: {},
          },
        });
      },
      nextStep: () => {
        set((state) => ({ 
          currentStep: Math.min(state.currentStep + 1, 3),
          state: { ...state.state, step: Math.min(state.currentStep + 1, 3) },
        }));
      },
      prevStep: () => {
        set((state) => ({ 
          currentStep: Math.max(state.currentStep - 1, 1),
          state: { ...state.state, step: Math.max(state.currentStep - 1, 1) },
        }));
      },
      updateState: (updates) => {
        set((state) => ({
          state: { ...state.state, ...updates },
        }));
      },
      completeOnboarding: () => {
        set({ isOnboarding: false, currentStep: 1 });
      },
      resetOnboarding: () => {
        set({ 
          isOnboarding: false, 
          currentStep: 1,
          state: {
            step: 1,
            selectedPacks: [],
            selectedChannels: [],
            channelConfigs: {},
          },
        });
      },
    }),
    {
      name: 'clawd-onboarding',
    }
  )
);

